import React from "react";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Stack from "@mui/material/Stack";
import DeleteIcon from "@mui/icons-material/Delete";
import Button from "@mui/material/Button";
import { makeStyles } from "@mui/styles";

const useStyles = makeStyles((theme) => ({
  tableContainer: {
    maxWidth: "100%",
    marginTop: theme.spacing(2),
    marginBottom: theme.spacing(2),
  },
  tableHeadCell: {
    fontWeight: "bold",
    backgroundColor: "lightskyblue",
  },
  tableRow: {
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
  },
}));

const PetTable = ({ pets, showButtons = true, handleEditClick, deletePet }) => {
  const classes = useStyles();

  return (
    <TableContainer component={Paper} className={classes.tableContainer}>
      <Table stickyHeader aria-label="sticky table">
        <TableHead>
          <TableRow>
            <TableCell className={classes.tableHeadCell}>Animal</TableCell>
            <TableCell className={classes.tableHeadCell}>Description</TableCell>
            <TableCell className={classes.tableHeadCell}>Age</TableCell>
            <TableCell className={classes.tableHeadCell}>Price</TableCell>
            {showButtons && (
              <TableCell className={classes.tableHeadCell}>Actions</TableCell>
            )}
          </TableRow>
        </TableHead>
        <TableBody>
          {pets.map((pet) => (
            <TableRow key={pet.id} className={classes.tableRow}>
              <TableCell>{pet.animal}</TableCell>
              <TableCell>{pet.description}</TableCell>
              <TableCell>{pet.age}</TableCell>
              <TableCell>{pet.price}</TableCell>
              {showButtons && (
                <TableCell>
                  <Stack direction="row" spacing={2}>
                    <Button
                      variant="outlined"
                      size="small"
                      onClick={() => handleEditClick(pet.id)}
                    >
                      Edit
                    </Button>
                    <Button
                      variant="outlined"
                      color="error"
                      size="small"
                      onClick={() => deletePet(pet.id)}
                      startIcon={<DeleteIcon />}
                    >
                      Delete
                    </Button>
                  </Stack>
                </TableCell>
              )}
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default PetTable;
