import React, { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";

function Pets() {
  const [isLoaded, setIsLoaded] = useState(false);
  const [pets, setPets] = useState([]);
  const [searchResults, setSearchResults] = useState([]);
  const [editPetId, setEditPetId] = useState(null); // To keep track of the pet being edited
  const [newPet, setNewPet] = useState({
    animal: "",
    description: "",
    age: "",
    price: "",
  });

  useEffect(() => {
    fetchPets();
  }, []);

  function fetchPets() {
    fetch("http://localhost:3001/api?act=getall")
      .then((res) => res.json())
      .then((result) => {
        setIsLoaded(true);
        setPets(result);
      });
  }

  function addPet() {
    fetch("http://localhost:3001/api?act=add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPet),
    })
      .then((res) => res.json())
      .then((result) => {
        fetchPets();
        setNewPet({
          animal: "",
          description: "",
          age: "",
          price: "",
        });
      });
  }

  function deletePet(id) {
    fetch(`http://localhost:3001/api?act=delete&id=${id}`)
      .then((res) => res.json())
      .then((result) => {
        fetchPets();
      });
  }

  function updatePet(id) {
    fetch(`http://localhost:3001/api?act=update&id=${id}`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(newPet),
    })
      .then((res) => res.json())
      .then((result) => {
        fetchPets();
        setEditPetId(null); // Reset edit mode
      });
  }

  function handleInputChange(event) {
    const { name, value } = event.target;
    setNewPet((prev) => ({
      ...prev,
      [name]: value,
    }));
  }

  return (
    <div>
      <h1>Pets</h1>
      <Paper sx={{ width: "100%", overflow: "hidden" }}>
        <TableContainer>
          <Table
            stickyHeader
            aria-label="sticky table"
            sx={{
              borderCollapse: "collapse",
              fontFamily: "Montserrat, sans-serif",
            }}
          >
            <TableHead>
              <TableRow>
                <TableCell
                  sx={{ fontWeight: "bold", backgroundColor: "lightskyblue" }}
                >
                  Animal
                </TableCell>
                <TableCell
                  sx={{ fontWeight: "bold", backgroundColor: "lightskyblue" }}
                >
                  Description
                </TableCell>
                <TableCell
                  sx={{ fontWeight: "bold", backgroundColor: "lightskyblue" }}
                >
                  Age
                </TableCell>
                <TableCell
                  sx={{ fontWeight: "bold", backgroundColor: "lightskyblue" }}
                >
                  Price
                </TableCell>
                <TableCell
                  sx={{ fontWeight: "bold", backgroundColor: "lightskyblue" }}
                >
                  Actions
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {pets.map((pet) => (
                <TableRow key={pet.id}>
                  {editPetId === pet.id ? (
                    <>
                      <TableCell>
                        <input
                          type="text"
                          name="animal"
                          value={newPet.animal}
                          onChange={handleInputChange}
                        />
                      </TableCell>
                      <TableCell>
                        <input
                          type="text"
                          name="description"
                          value={newPet.description}
                          onChange={handleInputChange}
                        />
                      </TableCell>
                      <TableCell>
                        <input
                          type="text"
                          name="age"
                          value={newPet.age}
                          onChange={handleInputChange}
                        />
                      </TableCell>
                      <TableCell>
                        <input
                          type="text"
                          name="price"
                          value={newPet.price}
                          onChange={handleInputChange}
                        />
                      </TableCell>
                      <TableCell>
                        <Button
                          variant="contained"
                          onClick={() => updatePet(pet.id)}
                        >
                          Save
                        </Button>
                      </TableCell>
                    </>
                  ) : (
                    <>
                      <TableCell>{pet.animal}</TableCell>
                      <TableCell>{pet.description}</TableCell>
                      <TableCell>{pet.age}</TableCell>
                      <TableCell>{pet.price}</TableCell>
                      <TableCell>
                        <Button
                          variant="outlined"
                          onClick={() => setEditPetId(pet.id)}
                        >
                          Edit
                        </Button>
                        <Button
                          variant="outlined"
                          color="error"
                          onClick={() => deletePet(pet.id)}
                        >
                          Delete
                        </Button>
                      </TableCell>
                    </>
                  )}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </Paper>
      <br />
      <h2>Functions</h2>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          addPet();
        }}
      >
        <input
          type="text"
          name="animal"
          value={newPet.animal}
          onChange={handleInputChange}
          placeholder="Animal"
          required
        />
        <input
          type="text"
          name="description"
          value={newPet.description}
          onChange={handleInputChange}
          placeholder="Description"
          required
        />
        <input
          type="number"
          name="age"
          value={newPet.age}
          onChange={handleInputChange}
          placeholder="Age"
          required
        />
        <input
          type="number"
          name="price"
          value={newPet.price}
          onChange={handleInputChange}
          placeholder="Price"
          required
        />
        <Button type="submit" variant="contained">
          Add Pet
        </Button>
      </form>
      <br />
      <h2>Search Results</h2>
      <pre>{searchResults}</pre>
    </div>
  );
}

export default Pets;
