import React from "react";
import Paper from "@mui/material/Paper";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Button from "@mui/material/Button";
import DeleteIcon from "@mui/icons-material/Delete";
import { styled } from "@mui/material/styles";

// Styled components
const StyledPaper = styled(Paper)({
  width: "100%",
  overflow: "hidden",
});

const StyledTableContainer = styled(TableContainer)({
  minWidth: 650,
});

const StyledTableCell = styled(TableCell)(({ theme }) => ({
  fontWeight: "bold",
  backgroundColor: theme.palette.primary.main,
  color: theme.palette.primary.contrastText,
}));

const StyledButton = styled(Button)(({ theme }) => ({
  marginRight: theme.spacing(1),
}));

const PetTable = ({ pets, showButtons = true, handleEditClick, deletePet }) => {
  return (
    <div>
      <StyledPaper>
        <StyledTableContainer>
          <Table stickyHeader aria-label="sticky table">
            <TableHead>
              <TableRow>
                <StyledTableCell>Animal</StyledTableCell>
                <StyledTableCell>Description</StyledTableCell>
                <StyledTableCell>Age</StyledTableCell>
                <StyledTableCell>Price</StyledTableCell>
                {showButtons && <StyledTableCell>Actions</StyledTableCell>}
              </TableRow>
            </TableHead>
            <TableBody>
              {pets.map((pet) => (
                <TableRow key={pet.id}>
                  <TableCell>{pet.animal}</TableCell>
                  <TableCell>{pet.description}</TableCell>
                  <TableCell>{pet.age}</TableCell>
                  <TableCell>{pet.price}</TableCell>
                  {showButtons && (
                    <TableCell>
                      <StyledButton
                        variant="outlined"
                        size="small"
                        onClick={() => handleEditClick(pet.id)}
                      >
                        Edit
                      </StyledButton>
                      <StyledButton
                        variant="outlined"
                        color="error"
                        size="small"
                        onClick={() => deletePet(pet.id)}
                        startIcon={<DeleteIcon />}
                      >
                        Delete
                      </StyledButton>
                    </TableCell>
                  )}
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </StyledTableContainer>
      </StyledPaper>
    </div>
  );
};

export default PetTable;
